create table book
(
    id        char(4)       not null,
    name      varchar(100)  not null,
    author    varchar(20)   null,
    inventory int           not null,
    attribute varchar(20)   null,
    type      varchar(20)   null,
    ISBN      varchar(20)   not null,
    price     int           not null,
    pid       char(4)       not null,
    sid       char(4)       not null,
    image     varchar(100)  null,
    hot       int default 0 null,
    constraint book_ISBN_uindex
        unique (ISBN),
    constraint book_id_uindex
        unique (id),
    constraint book_publisher_id_fk
        foreign key (pid) references publisher (id),
    constraint book_suppliers_id_fk
        foreign key (sid) references suppliers (id)
);

create index book_name
    on book (name);

alter table book
    add primary key (id);

