create table inbound
(
    id    int auto_increment
        primary key,
    name  varchar(100) not null,
    count int          not null,
    sid   char(4)      not null,
    price int          not null,
    time  datetime     null,
    bid   char(4)      null,
    constraint inbound_book_id_fk
        foreign key (bid) references book (id),
    constraint inbound_suppliers_id_fk
        foreign key (sid) references suppliers (id)
)
    comment '入库记录';

